import axios from "axios";
import React, { useState } from "react";

function App() {
  const url =
    "https://api.openweathermap.org/data/2.5/weather?q=tokyo&appid=5efa27fba902792fc106c92d65695eed";

  return (
    <div className="app">
      <div className="container">
        <div className="top">
          <div className="location">
            <p>Tokyo</p>
          </div>
          <div className="temp">
            <h1>20℃</h1>
          </div>
          <div className="decription">
            <p>Clouds</p>
          </div>
        </div>
        <div className="bottom"></div>
      </div>
    </div>
  );
}

export default App;
